import React, { useEffect } from 'react'
import { storiesOf } from '@storybook/react'
import { action } from '@storybook/addon-actions'

import { ConfigContext } from '../../utils/withConfig'
import { WindowContext } from '../../hooks/browser/useWindow'
import CheckoutContent from '../../components/content/CheckoutContent'
import { PostMessages } from '../../messageTypes'

import configure from '../../config'

const lockAddress1 = '0x1234567890123456789012345678901234567890'
const lockAddress2 = '0x4567890123456789012345678901234567890123'
const lockAddress3 = '0x7890123456789012345678901234567890123456'
const paywallConfig = {
  icon:
    'data:image/svg+xml;charset=utf-8;base64,',
  callToAction: {
    default:
      'Enjoy Forbes Online without any ads for as little as $2 a week. \nPay with Ethereum in just two clicks.',
  },
  locks: {
    [lockAddress1]: {
      name: 'One Week',
    },
    [lockAddress2]: {
      name: 'One Month',
    },
    [lockAddress3]: {
      name: 'One Year',
    },
  },
}

const locks = {
  [lockAddress1]: {
    name: 'Weekly',
    address: lockAddress1,
    keyPrice: '0.1',
    expirationDuration: 60 * 60 * 24 * 7,
    key: {
      status: 'none',
      transactions: [],
      expiration: 0,
      confirmations: 0,
      owner: lockAddress1,
      lock: lockAddress1,
    },
  },
  [lockAddress2]: {
    name: 'Monthly',
    address: lockAddress2,
    keyPrice: '0.3',
    expirationDuration: 60 * 60 * 24 * 30,
    key: {
      status: 'none',
      transactions: [],
      expiration: 0,
      confirmations: 0,
      owner: lockAddress1,
      lock: lockAddress2,
    },
  },
  [lockAddress3]: {
    name: 'Yearly',
    address: lockAddress3,
    keyPrice: '3',
    expirationDuration: 60 * 60 * 24 * 365,
    key: {
      status: 'none',
      transactions: [],
      expiration: 0,
      confirmations: 0,
      owner: lockAddress1,
      lock: lockAddress3,
    },
  },
}

const config = {
  ...configure(),
  requiredNetworkId: 1,
  isServer: false,
  isInIframe: true,
}

const fakeWindow = {
  location: {
    pathname: '/',
    hash: '',
    search: '?origin=origin',
  },
  handlers: {
    message: new Map(),
  },
  parent: {
    postMessage: () => {
      action('postMessage')
    },
  },
  console: window.console,
  addEventListener: (type, cb) => fakeWindow.handlers[type].set(cb, cb),
  removeEventListener: (type, cb) =>
    delete fakeWindow.handlers[type].delete(cb),
}

storiesOf('Checkout page', module)
  // pass in a fake window object, to avoid modifying the real body and munging storyshots
  .addDecorator(getStory => (
    <ConfigContext.Provider value={config}>
      <WindowContext.Provider value={fakeWindow}>
        {getStory()}
      </WindowContext.Provider>
    </ConfigContext.Provider>
  ))
  .add('Checkout page', () => {
    // set the data needed to display the checkout
    const Wrapper = () => {
      useEffect(() => {
        const messageTemplate = {
          type: 'message',
          source: fakeWindow.parent,
          origin: 'origin',
        }
        fakeWindow.handlers.message.forEach(postedMessage => {
          postedMessage({
            ...messageTemplate,
            data: {
              type: PostMessages.CONFIG,
              payload: paywallConfig,
            },
          })
          setTimeout(() => {
            postedMessage({
              ...messageTemplate,
              data: {
                type: PostMessages.UPDATE_ACCOUNT,
                payload: lockAddress1,
              },
            })
            postedMessage({
              ...messageTemplate,
              data: {
                type: PostMessages.UPDATE_ACCOUNT_BALANCE,
                payload: '889',
              },
            })
            postedMessage({
              ...messageTemplate,
              data: {
                type: PostMessages.UPDATE_LOCKS,
                payload: locks,
              },
            })
            postedMessage({
              ...messageTemplate,
              data: {
                type: PostMessages.UPDATE_NETWORK,
                payload: 1,
              },
            })
          })
        })
      })
      return <CheckoutContent />
    }
    return <Wrapper />
  })
  .add('Checkout page, wrong network', () => {
    // set the data needed to display the checkout
    const Wrapper = () => {
      useEffect(() => {
        const messageTemplate = {
          type: 'message',
          source: fakeWindow.parent,
          origin: 'origin',
        }
        fakeWindow.handlers.message.forEach(postedMessage => {
          postedMessage({
            ...messageTemplate,
            data: {
              type: PostMessages.CONFIG,
              payload: paywallConfig,
            },
          })
          setTimeout(() => {
            postedMessage({
              ...messageTemplate,
              data: {
                type: PostMessages.UPDATE_ACCOUNT,
                payload: lockAddress1,
              },
            })
            postedMessage({
              ...messageTemplate,
              data: {
                type: PostMessages.UPDATE_ACCOUNT_BALANCE,
                payload: '889',
              },
            })
            postedMessage({
              ...messageTemplate,
              data: {
                type: PostMessages.UPDATE_LOCKS,
                payload: locks,
              },
            })
            postedMessage({
              ...messageTemplate,
              data: {
                type: PostMessages.UPDATE_NETWORK,
                payload: 2,
              },
            })
          })
        })
      })
      return <CheckoutContent />
    }
    return <Wrapper />
  })
  .add('Checkout page, no wallet', () => {
    // set the data needed to display the checkout
    const Wrapper = () => {
      useEffect(() => {
        const messageTemplate = {
          type: 'message',
          source: fakeWindow.parent,
          origin: 'origin',
        }
        fakeWindow.handlers.message.forEach(postedMessage => {
          postedMessage({
            ...messageTemplate,
            data: {
              type: PostMessages.CONFIG,
              payload: paywallConfig,
            },
          })
          setTimeout(() => {
            postedMessage({
              ...messageTemplate,
              data: {
                type: PostMessages.UPDATE_ACCOUNT,
                payload: null,
              },
            })
            postedMessage({
              ...messageTemplate,
              data: {
                type: PostMessages.UPDATE_ACCOUNT_BALANCE,
                payload: '0',
              },
            })
            postedMessage({
              ...messageTemplate,
              data: {
                type: PostMessages.UPDATE_LOCKS,
                payload: locks,
              },
            })
            postedMessage({
              ...messageTemplate,
              data: {
                type: PostMessages.UPDATE_NETWORK,
                payload: 1,
              },
            })
          })
        })
      })
      return <CheckoutContent />
    }
    return <Wrapper />
  })
